html:
{% set settings = frappe.get_single('Shop Settings') %}
<!DOCTYPE html>
<html>
<head>
<meta charset="utf-8">
<style>
  body {
    font-family: 'Courier New', monospace;
    font-size: 12px;
    margin: 0;
    padding: 8px;
    width: 72mm;
  }
  .center { text-align: center; }
  .right { text-align: right; }
  .bold { font-weight: bold; }
  .divider { border-top: 1px dashed #000; margin: 6px 0; }
  table { width: 100%; border-collapse: collapse; }
  td { padding: 2px 0; vertical-align: top; }
  td.qty { width: 6%; }
  td.item { width: 55%; }
  td.price { width: 18%; text-align: right; }
  td.total { width: 21%; text-align: right; }
  .summary-row td { padding: 1px 0; }
  .grand-total td { font-weight: bold; font-size: 14px; padding-top: 4px; }
</style>
</head>
<body>

<div class="center bold" style="font-size:15px;">{{ settings.shop_name or doc.company }}</div>
{% if settings.location %}
<div class="center">{{ settings.location }}</div>
{% endif %}
{% if settings.owner_phone %}
<div class="center">Tel: {{ settings.owner_phone }}</div>
{% endif %}
{% if settings.vat_tin %}
<div class="center">TIN: {{ settings.vat_tin }}</div>
{% endif %}

<div class="divider"></div>

<table>
  <tr>
    <td>Receipt #:</td>
    <td class="right">{{ doc.name }}</td>
  </tr>
  <tr>
    <td>Date:</td>
    <td class="right">{{ frappe.utils.formatdate(doc.posting_date, 'dd/MM/yyyy') }}</td>
  </tr>
  <tr>
    <td>Customer:</td>
    <td class="right">{{ doc.customer }}</td>
  </tr>
  {% if doc.custom_payment_method %}
  <tr>
    <td>Payment:</td>
    <td class="right">{{ doc.custom_payment_method }}</td>
  </tr>
  {% endif %}
</table>

<div class="divider"></div>

<table>
  <thead>
    <tr>
      <td class="qty bold">Qty</td>
      <td class="item bold">Item</td>
      <td class="price bold">Price</td>
      <td class="total bold">Total</td>
    </tr>
  </thead>
  <tbody>
  {% for item in doc.items %}
    <tr>
      <td class="qty">{{ item.qty | int }}</td>
      <td class="item">{{ item.item_name }}</td>
      <td class="price">{{ '{:,.2f}'.format(item.rate) }}</td>
      <td class="total">{{ '{:,.2f}'.format(item.amount) }}</td>
    </tr>
  {% endfor %}
  </tbody>
</table>

<div class="divider"></div>

<table class="summary-row">
  <tr>
    <td>Subtotal</td>
    <td class="right">ETB {{ '{:,.2f}'.format(doc.net_total) }}</td>
  </tr>
  {% for tax in doc.taxes %}
  {% if tax.tax_amount %}
  <tr>
    <td>{{ tax.description }}</td>
    <td class="right">ETB {{ '{:,.2f}'.format(tax.tax_amount) }}</td>
  </tr>
  {% endif %}
  {% endfor %}
</table>

<div class="divider"></div>

<table class="grand-total">
  <tr>
    <td>TOTAL</td>
    <td class="right">ETB {{ '{:,.2f}'.format(doc.grand_total) }}</td>
  </tr>
</table>

{% if doc.outstanding_amount > 0 %}
<div class="divider"></div>
<table class="summary-row">
  <tr>
    <td>Paid</td>
    <td class="right">ETB {{ '{:,.2f}'.format(doc.grand_total - doc.outstanding_amount) }}</td>
  </tr>
  <tr>
    <td class="bold">Balance Due</td>
    <td class="right bold">ETB {{ '{:,.2f}'.format(doc.outstanding_amount) }}</td>
  </tr>
</table>
{% endif %}

<div class="divider"></div>
<div class="center" style="margin-top:8px;">Thank you for your purchase!</div>
<div class="center" style="color:#888;margin-top:4px;font-size:10px;">Powered by Gebeya ERP</div>

</body>
</html>
